{-# LANGUAGE NoImplicitPrelude #-}
{-# LANGUAGE OverloadedStrings #-}
{-# OPTIONS_GHC -fno-warn-unused-imports #-}
{-# OPTIONS_GHC -fno-warn-name-shadowing #-}
module Boris.Http.Template.Page.Build where
import qualified Projector.Html.Runtime
import Boris.Http.Template.Data.Build.Data
pageBuild :: BuildStatus -> Build -> Projector.Html.Runtime.Html
pageBuild = \status -> \build -> Projector.Html.Runtime.foldHtml [case status of
                                                                      BuildOk -> Projector.Html.Runtime.foldHtml [Projector.Html.Runtime.parentNode (Projector.Html.Runtime.Tag "div") (Projector.Html.Runtime.fold [[Projector.Html.Runtime.Attribute (Projector.Html.Runtime.AttributeKey "style") (Projector.Html.Runtime.AttributeValue "float:right; height:50px; width: 50px; background-color: #16CC55; border-radius: 10px;")]]) (Projector.Html.Runtime.foldHtml []),
                                                                                                                  Projector.Html.Runtime.textNodeUnescaped " "]
                                                                      BuildKo -> Projector.Html.Runtime.foldHtml [Projector.Html.Runtime.textNodeUnescaped " ",
                                                                                                                  Projector.Html.Runtime.parentNode (Projector.Html.Runtime.Tag "div") (Projector.Html.Runtime.fold [[Projector.Html.Runtime.Attribute (Projector.Html.Runtime.AttributeKey "style") (Projector.Html.Runtime.AttributeValue "float:right; height:50px; width: 50px; background-color: #ef6464; border-radius: 10px;")]]) (Projector.Html.Runtime.foldHtml []),
                                                                                                                  Projector.Html.Runtime.textNodeUnescaped " "]
                                                                      BuildUndecided -> Projector.Html.Runtime.foldHtml [Projector.Html.Runtime.textNodeUnescaped " ",
                                                                                                                         Projector.Html.Runtime.parentNode (Projector.Html.Runtime.Tag "div") (Projector.Html.Runtime.fold [[Projector.Html.Runtime.Attribute (Projector.Html.Runtime.AttributeKey "style") (Projector.Html.Runtime.AttributeValue "float:right; height:50px; width: 50px; background-color: #c9c1c1; border-radius: 10px;")]]) (Projector.Html.Runtime.foldHtml []),
                                                                                                                         Projector.Html.Runtime.textNodeUnescaped " "],
                                                                  Projector.Html.Runtime.textNodeUnescaped "  ",
                                                                  Projector.Html.Runtime.parentNode (Projector.Html.Runtime.Tag "h2") (Projector.Html.Runtime.fold []) (Projector.Html.Runtime.parentNode (Projector.Html.Runtime.Tag "a") (Projector.Html.Runtime.fold [[Projector.Html.Runtime.Attribute (Projector.Html.Runtime.AttributeKey "href") (Projector.Html.Runtime.AttributeValue (Projector.Html.Runtime.concat ["/build/",
                                                                                                                                                                                                                                                                                                                                                                                                                               buildId build]))]]) (Projector.Html.Runtime.foldHtml [Projector.Html.Runtime.textNodeUnescaped "#",
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     Projector.Html.Runtime.textNode (buildId build)])),
                                                                  Projector.Html.Runtime.textNodeUnescaped "  ",
                                                                  case buildLog build of
                                                                      HasLog -> Projector.Html.Runtime.foldHtml [Projector.Html.Runtime.parentNode (Projector.Html.Runtime.Tag "div") (Projector.Html.Runtime.fold []) (Projector.Html.Runtime.parentNode (Projector.Html.Runtime.Tag "a") (Projector.Html.Runtime.fold [[Projector.Html.Runtime.Attribute (Projector.Html.Runtime.AttributeKey "href") (Projector.Html.Runtime.AttributeValue (Projector.Html.Runtime.concat ["/build/",
                                                                                                                                                                                                                                                                                                                                                                                                                                                                               buildId build,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                               "/log"]))]]) (Projector.Html.Runtime.textNodeUnescaped "[log]")),
                                                                                                                 Projector.Html.Runtime.textNodeUnescaped " "]
                                                                      NoLog -> Projector.Html.Runtime.foldHtml [Projector.Html.Runtime.textNodeUnescaped " ",
                                                                                                                Projector.Html.Runtime.foldHtml [],
                                                                                                                Projector.Html.Runtime.textNodeUnescaped " "],
                                                                  Projector.Html.Runtime.textNodeUnescaped "  ",
                                                                  Projector.Html.Runtime.parentNode (Projector.Html.Runtime.Tag "div") (Projector.Html.Runtime.fold []) (Projector.Html.Runtime.foldHtml [Projector.Html.Runtime.textNodeUnescaped "project: ",
                                                                                                                                                                                                          Projector.Html.Runtime.parentNode (Projector.Html.Runtime.Tag "a") (Projector.Html.Runtime.fold [[Projector.Html.Runtime.Attribute (Projector.Html.Runtime.AttributeKey "href") (Projector.Html.Runtime.AttributeValue (Projector.Html.Runtime.concat ["/project/",
                                                                                                                                                                                                                                                                                                                                                                                                                                                                 buildProject build]))]]) (Projector.Html.Runtime.textNode (buildProject build))]),
                                                                  Projector.Html.Runtime.textNodeUnescaped " ",
                                                                  Projector.Html.Runtime.parentNode (Projector.Html.Runtime.Tag "div") (Projector.Html.Runtime.fold []) (Projector.Html.Runtime.foldHtml [Projector.Html.Runtime.textNodeUnescaped "build: ",
                                                                                                                                                                                                          Projector.Html.Runtime.parentNode (Projector.Html.Runtime.Tag "a") (Projector.Html.Runtime.fold [[Projector.Html.Runtime.Attribute (Projector.Html.Runtime.AttributeKey "href") (Projector.Html.Runtime.AttributeValue (Projector.Html.Runtime.concat ["/project/",
                                                                                                                                                                                                                                                                                                                                                                                                                                                                 buildProject build,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                 "/build/",
                                                                                                                                                                                                                                                                                                                                                                                                                                                                 buildBuild build]))]]) (Projector.Html.Runtime.textNode (buildBuild build))]),
                                                                  Projector.Html.Runtime.textNodeUnescaped "  ",
                                                                  case buildRef build of
                                                                      Projector.Html.Runtime.Nothing -> Projector.Html.Runtime.foldHtml [Projector.Html.Runtime.foldHtml [],
                                                                                                                                         Projector.Html.Runtime.textNodeUnescaped " "]
                                                                      Projector.Html.Runtime.Just ref -> Projector.Html.Runtime.foldHtml [Projector.Html.Runtime.textNodeUnescaped " ",
                                                                                                                                          Projector.Html.Runtime.parentNode (Projector.Html.Runtime.Tag "div") (Projector.Html.Runtime.fold []) (Projector.Html.Runtime.foldHtml [Projector.Html.Runtime.textNodeUnescaped "ref: ",
                                                                                                                                                                                                                                                                                  Projector.Html.Runtime.textNode ref]),
                                                                                                                                          Projector.Html.Runtime.textNodeUnescaped " "],
                                                                  Projector.Html.Runtime.textNodeUnescaped "  ",
                                                                  case buildCommit build of
                                                                      Projector.Html.Runtime.Nothing -> Projector.Html.Runtime.foldHtml [Projector.Html.Runtime.foldHtml [],
                                                                                                                                         Projector.Html.Runtime.textNodeUnescaped " "]
                                                                      Projector.Html.Runtime.Just commit -> Projector.Html.Runtime.foldHtml [Projector.Html.Runtime.textNodeUnescaped " ",
                                                                                                                                             Projector.Html.Runtime.parentNode (Projector.Html.Runtime.Tag "div") (Projector.Html.Runtime.fold []) (Projector.Html.Runtime.foldHtml [Projector.Html.Runtime.textNodeUnescaped "commit: ",
                                                                                                                                                                                                                                                                                     Projector.Html.Runtime.parentNode (Projector.Html.Runtime.Tag "a") (Projector.Html.Runtime.fold [[Projector.Html.Runtime.Attribute (Projector.Html.Runtime.AttributeKey "href") (Projector.Html.Runtime.AttributeValue (Projector.Html.Runtime.concat ["/project/",
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            buildProject build,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            "/commit/",
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            commit]))]]) (Projector.Html.Runtime.textNode commit)]),
                                                                                                                                             Projector.Html.Runtime.textNodeUnescaped " "]]
