{-# LANGUAGE NoImplicitPrelude #-}
{-# LANGUAGE OverloadedStrings #-}
{-# OPTIONS_GHC -fno-warn-unused-imports #-}
{-# OPTIONS_GHC -fno-warn-name-shadowing #-}
module Boris.Http.Template.Page.Project where
import qualified Projector.Html.Runtime
import Boris.Http.Template.Data.Build.Data
import Boris.Http.Template.Data.Commit.Data
import Boris.Http.Template.Data.Project.Data
pageProject :: Project -> Projector.Html.Runtime.Html
pageProject = \project -> Projector.Html.Runtime.foldHtml [Projector.Html.Runtime.parentNode (Projector.Html.Runtime.Tag "h2") (Projector.Html.Runtime.fold []) (Projector.Html.Runtime.parentNode (Projector.Html.Runtime.Tag "a") (Projector.Html.Runtime.fold [[Projector.Html.Runtime.Attribute (Projector.Html.Runtime.AttributeKey "href") (Projector.Html.Runtime.AttributeValue (Projector.Html.Runtime.concat ["/project/",
                                                                                                                                                                                                                                                                                                                                                                                                                        projectName project]))]]) (Projector.Html.Runtime.textNode (projectName project))),
                                                           Projector.Html.Runtime.textNodeUnescaped "  ",
                                                           Projector.Html.Runtime.foldHtml (Projector.Html.Runtime.fmap (\build -> Projector.Html.Runtime.foldHtml [Projector.Html.Runtime.parentNode (Projector.Html.Runtime.Tag "div") (Projector.Html.Runtime.fold []) (Projector.Html.Runtime.parentNode (Projector.Html.Runtime.Tag "a") (Projector.Html.Runtime.fold [[Projector.Html.Runtime.Attribute (Projector.Html.Runtime.AttributeKey "href") (Projector.Html.Runtime.AttributeValue (Projector.Html.Runtime.concat ["/project/",
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  projectName project,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  "/build/",
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  build]))]]) (Projector.Html.Runtime.foldHtml [Projector.Html.Runtime.textNode (projectName project),
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                Projector.Html.Runtime.textNodeUnescaped " : ",
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                Projector.Html.Runtime.textNode build])),
                                                                                                                                                                    Projector.Html.Runtime.textNodeUnescaped " "]) (projectBuilds project))]
