{-# LANGUAGE NoImplicitPrelude #-}
{-# LANGUAGE OverloadedStrings #-}
{-# OPTIONS_GHC -fno-warn-unused-imports #-}
{-# OPTIONS_GHC -fno-warn-name-shadowing #-}
module Boris.Http.Template.Page.Status where
import qualified Projector.Html.Runtime
import Boris.Http.Template.Data.Build.Data
import Boris.Http.Template.Data.Commit.Data
import Boris.Http.Template.Data.Project.Data
import Boris.Http.Template.Page.Builds.Data
import Boris.Http.Template.Page.Status.Data
pageStatus :: Status -> Projector.Html.Runtime.Html
pageStatus = \status -> case Projector.Html.Runtime.isEmpty (statusBuilds status) of
                            Projector.Html.Runtime.True -> Projector.Html.Runtime.foldHtml [Projector.Html.Runtime.parentNode (Projector.Html.Runtime.Tag "p") (Projector.Html.Runtime.fold []) (Projector.Html.Runtime.textNodeUnescaped "All builds are green."),
                                                                                            Projector.Html.Runtime.textNodeUnescaped " "]
                            Projector.Html.Runtime.False -> Projector.Html.Runtime.foldHtml [Projector.Html.Runtime.textNodeUnescaped " ",
                                                                                             Projector.Html.Runtime.parentNode (Projector.Html.Runtime.Tag "p") (Projector.Html.Runtime.fold []) (Projector.Html.Runtime.foldHtml [Projector.Html.Runtime.textNodeUnescaped " Builds that are currently borked: ",
                                                                                                                                                                                                                                   Projector.Html.Runtime.foldHtml (Projector.Html.Runtime.fmap (\b -> Projector.Html.Runtime.foldHtml [Projector.Html.Runtime.parentNode (Projector.Html.Runtime.Tag "div") (Projector.Html.Runtime.fold []) (Projector.Html.Runtime.foldHtml [Projector.Html.Runtime.textNodeUnescaped " ",
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                Projector.Html.Runtime.parentNode (Projector.Html.Runtime.Tag "a") (Projector.Html.Runtime.fold [[Projector.Html.Runtime.Attribute (Projector.Html.Runtime.AttributeKey "href") (Projector.Html.Runtime.AttributeValue (Projector.Html.Runtime.concat ["/project/",
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       statusBuildProject b]))]]) (Projector.Html.Runtime.textNode (statusBuildProject b)),
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                Projector.Html.Runtime.textNodeUnescaped " : ",
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                Projector.Html.Runtime.parentNode (Projector.Html.Runtime.Tag "a") (Projector.Html.Runtime.fold [[Projector.Html.Runtime.Attribute (Projector.Html.Runtime.AttributeKey "href") (Projector.Html.Runtime.AttributeValue (Projector.Html.Runtime.concat ["/project/",
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       statusBuildProject b,
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       "/build/",
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       statusBuildBuild b]))]]) (Projector.Html.Runtime.textNode (statusBuildBuild b)),
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                Projector.Html.Runtime.textNodeUnescaped " : ",
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                Projector.Html.Runtime.parentNode (Projector.Html.Runtime.Tag "a") (Projector.Html.Runtime.fold [[Projector.Html.Runtime.Attribute (Projector.Html.Runtime.AttributeKey "href") (Projector.Html.Runtime.AttributeValue (Projector.Html.Runtime.concat ["/build/",
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       statusBuildId b]))]]) (Projector.Html.Runtime.textNode (statusBuildId b)),
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                Projector.Html.Runtime.textNodeUnescaped " "]),
                                                                                                                                                                                                                                                                                                                                        Projector.Html.Runtime.textNodeUnescaped " "]) (statusBuilds status)),
                                                                                                                                                                                                                                   Projector.Html.Runtime.textNodeUnescaped " "]),
                                                                                             Projector.Html.Runtime.textNodeUnescaped " "]
